<script <?= $nonce['script'] ?? '' ?>>
document.getElementById('button-addon2').addEventListener('click', function() {
    var email = document.getElementById('email').value;
    var referral = document.getElementById('referral').value;

    fetch('Subscribe/Submit', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: 'email=' + encodeURIComponent(email) + '&referral=' + encodeURIComponent(referral)
    })
    .then(response => response.text()) // or response.json() if your server returns JSON
    .then(data => {
        console.log(data); // Handle the response data here
        // Perform actions based on the response (e.g., update the DOM, show a message, redirect, etc.)
    })
    .catch(error => {
        console.error('Error:', error);
    });
});
</script>